import { DefaultSession } from "next-auth";

import "next-auth/jwt";

declare module "next-auth" {
  interface Session {
    user: {
      role?: string;
    } & DefaultSession["admin"];
  }

  interface User {
    role?: string;
  }

  declare module "next-auth/jwt" {
    interface JWT {
      role?: string;
    }
  }
}
